import React from 'react';
import './Home.css';
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import {useEffect, useState } from 'react';
// import {useSelector} from 'react-redux';
import {initialState} from '../../Features/Inventory/inventoryAPI';
import { AnimatePresence } from "framer-motion";
import RenderPrice from './RenderPrice';
import './BargainB.png';
import Row from './rowRender';
function renderShowcase() {
    const array = [...initialState["t-shirts"], ...initialState["shirts"]]
    const One = array[Math.floor(Math.random()*20)];
    array.splice(array.indexOf(One),1);
    const Two = array[Math.floor(Math.random()*19)];
    array.splice(array.indexOf(Two),1);
    const Three = array[Math.floor(Math.random()*17)];
    return [One,Two,Three];
}

function Home () {
        const [mainWindow,setMainWindow] = useState([initialState["t-shirts"][Math.floor(Math.random()*10)], initialState["t-shirts"][Math.floor(Math.random()*10)],initialState["t-shirts"][Math.floor(Math.random()*10)]]);
        const [remain,setRemain] = useState({});
        const [maxCount,setMaxCount] = useState(3);
        const summer = initialState["shirts"].map(e => e);
        const pantsOnSale = initialState["pants"].map(e => e);
        useEffect(()=> {
            const shirt = setInterval(()=> {
            if (mainWindow.includes(remain)) {
                setMainWindow((prev)=> {
                    return prev.map((obj,index)=> {
                        if (obj === remain && index ===  mainWindow.indexOf(remain)) {
                            return remain;
                        } 
                        return [...initialState["t-shirts"], ...initialState["shirts"]].filter(item => item.name !== remain.name)[Math.floor(Math.random()*19)];
                    })
                })
            } else {
            
                
                setMainWindow(renderShowcase());
            }
        },3000);

        return () => {
            clearInterval(shirt); 
        }
    });
    function handleHover(event) {
        const hoverItem = initialState["t-shirts"].find((item) => {
            return item.name.split(' ').join('') === event.target.id;
        });
        setRemain(hoverItem);
        // event.target.style.transition = '1s';
        // event.target.style.backgroundColor = 'rgba(200,0,0,0.6)';
        // event.target.style.padding = '15px';
    }
    function handleLeave(event) {
    setRemain({});
       
    }
    return (<><motion.div
        className='background'
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1 }}
      >
        <h1><i>Our Lastest Deals...</i></h1>
        <h2>Check Out Our T-shirts! </h2>
        <div className="mainwindow">
        <AnimatePresence>
            {mainWindow.map((div) => {
        return (<Link  className="a" to={"/product/" + div.name.split(' ').join('+')}><motion.span
        key={div.name}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1.7 }}
      ><div id={div.name.split(' ').join('')} onMouseLeave={handleLeave}  onMouseEnter={handleHover}  ><img alt={div.name} id={div.name.split(' ').join('')} onMouseLeave={handleLeave}  onMouseEnter={handleHover} src={div.image} />
      <AnimatePresence>
      <RenderPrice hover={remain} id={div} price={div.price} name={div.name} /> 
      </AnimatePresence>
      </div>
      
      </motion.span> 
      </Link>)
    })
    }
        </AnimatePresence>
        </div>
        <h2>Best Deals!</h2>
        <div class="categoryContainer" >
            <h2 style={{textAlign:"left", marginLeft:'100px'}}>Shirts on sale 20% off!</h2>
            <div className="flexContainer">
                {Array(1).fill(0).map((pages) => {
                    const page = [summer.filter(div => {
                        return div.price < 30.99;
                    }).map((div,index) => {
                        if (index <= maxCount) {
                            summer.splice(summer.indexOf(div),1);
                            return  <Row div={div} />
                        }
                        return '';
                        }
                        ).filter(element => element !== '')];
                        
                    return <div className="row">{page}</div>;
                })}
            </div>

        </div>
        <div class="categoryContainer"  >
            <h2 style={{textAlign:"left", marginLeft:'100px'}}>Pants on sale 20% off!</h2>
            <div  className="flexContainer">
                {Array(1).fill(0).map((pages) => {
                   const page = [pantsOnSale.filter(div => {
                    return div.price < 40;
                }).map((div,index) => {
                    if (index <= maxCount) {
                        pantsOnSale.splice(pantsOnSale.indexOf(div),1);
                        return <Row  div={div} />
                    }
                    return <></>;
                    }
                    )]
                    return <div className="row">{page}</div>;
                })}
            </div>
            
        </div>
        </motion.div></>)

}

export default Home;
