import React from 'react';
import { Routes, Route, useParams } from 'react-router-dom';
import './ProductView.css';
import ProductDescription from '../ProductView/ProductDescription';
import { useSelector } from 'react-redux';
import { AnimatePresence } from 'framer-motion';
import { allFilteredClothing } from '../../Features/Inventory/inventorySlice';
function ProductView() {
    const {product} = useParams();
    const productview = product.split('+').join(' ');
    const productSelector = useSelector(allFilteredClothing);
    const productObjArr = Object.values(productSelector);
    const productObj = productObjArr.filter(i => i.find(p => p.name === productview))[0].find(p => p.name === productview);

    return (<div>
        <AnimatePresence>
       <ProductDescription productobj={productObj} producttitle={productview} />
       </AnimatePresence>
    </div>)

}

export default ProductView;
