import React from 'react';
import './Root.css';
import { Outlet, NavLink, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import {useEffect, useState} from 'react';
import { useDispatch } from 'react-redux';
import { searchTerm } from '../../Features/ProductFilter/searchFilterSlice';
import { cartSelector} from '../../Features/Cart/cartSlice';
import { useSelector } from 'react-redux';
import Cart from '../Cart/Cart';
import {AnimatePresence} from 'framer-motion';

function Root ({setTerm, term}) {
    const firstRender = useNavigate();
    const dispatch = useDispatch();
    const [viewCart,setViewCart] = useState(false);
    const [notif,setNotif] = useState(0);
    const cart = useSelector(cartSelector);
    function handleText (event) {
        const {name,value} = event.target;
        dispatch(searchTerm(value));
        setTerm((prev) => {
            return { ...prev, [name]: value}
        })
    }

    useEffect(()=> {

        firstRender('/home');
    },[])
    useEffect(()=> {
        if (cart.length) {
            setNotif(cart.length);
        } else {
            setNotif(cart.length);
        }
    },[cart.length])
    return (<>
     <motion.div
            className="container text-center  bg-black"
            initial={{ opacity: 0, transform: `translate(100px)` }}
            animate={{ opacity: 1 , transform: `translate(0px)`}}
            exit={{ opacity: 0 }}
            transition={{ duration: 1 }}
          >
    <nav className="navigation">
        <span className="logoContainer"><b id="logo"><NavLink className="navlinks" to="/home">Bargain Boutique</NavLink></b>
        <ul>
            <li><NavLink className="navlinks animate" to="/home">Home</NavLink></li>
            <li><NavLink className="navlinks animate" to="/products">Products</NavLink></li>
            <li><NavLink className="navlinks animate" to="/about#contacts">Contact</NavLink></li>
            <li><NavLink className="navlinks animate" to="/about"> About</NavLink></li>
        </ul>
        </span>
        <div id="searchContainer">
                <input type="text" name="search" value={term['search']} onChange={handleText} />
                <button id="cartButton" onClick={() => { 
                    setViewCart(!viewCart);
                    
                }} ><b>Cart</b>{ notif ? <i className="notification">{notif}</i> : ''}</button>
                
        </div>

            
    </nav>
    <AnimatePresence >
    <Cart view={viewCart}  />
    </AnimatePresence >
    <Outlet />
    </motion.div> </>)

}

export default Root;
