import './Cart.css';
import React from 'react';
import { useSelector } from 'react-redux';
import CartItem from './CartItem';
import { cartSelector,removeCartItem } from '../../Features/Cart/cartSlice';
import { motion } from 'framer-motion';
import { useDispatch } from 'react-redux';
import { useEffect,useState } from 'react';
import { addQuantity } from '../../Features/Cart/cartSlice';
function Cart ({view}) {
    const cartobj = useSelector(cartSelector);
    const [quantity,setQuantity]= useState(1);
    const [cartItemObj,setCartItemObj] = useState({})
    const dispatch = useDispatch();
    function clearCart() {
        cartobj.forEach(obj => {
            dispatch(removeCartItem(obj));
        });
    }
    function handleQuantity (e) {
        const {value} = e.target;
        setQuantity(value)
        dispatch(addQuantity({...cartObj, quantity: value}));
    }
    if (view) {
    return (
        <motion.div
        className="cartCon"
        initial={{ opacity: 0 }}
        animate={{ transform: `scaleY(1)`,opacity: 1}}
        exit={{ opacity: 0 }}
        transition={{ duration: 0.5 }}
      ><div className="cart">

        <div className="cartContainer">
            {cartobj.map(obj => {
                return  <CartItem quantity={quantity} handleQuantity={handleQuantity} cart={cartobj} cartbj={obj} view={view} />;
            })}
        </div>
        <div id="container2">
            <button>Checkout</button>
            <button onClick={clearCart}>Clear Cart</button>
        </div>
    </div></motion.div>)
    }
    return '';
   
}

export default Cart;
