import React from 'react';
import './Home.css';
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import {useEffect, useState } from 'react';
import {useSelector} from 'react-redux';
import {initialState} from '../../Features/Inventory/inventoryAPI';
import { AnimatePresence } from "framer-motion";
import RenderPrice from './RenderPrice';
import './BargainB.png';
import Row from './rowRender';
import { inventorySelector } from '../../Features/Inventory/inventorySlice';

function renderShowcase(inventory) {
    const array = [...inventory["t-shirts"], ...inventory["shirts"]]
    const One = array[Math.floor(Math.random()*20)];
    array.splice(array.indexOf(One),1);
    const Two = array[Math.floor(Math.random()*19)];
    array.splice(array.indexOf(Two),1);
    const Three = array[Math.floor(Math.random()*17)];
    return [One,Two,Three];
}

function Home () {
        // Variables reflecting current inventory state
        const inventory = useSelector(inventorySelector);
        const tShirtSales = inventory["t-shirts"].map(e => e);
        const shirtSales = inventory["shirts"].map(e => e);
        const pantsSales = inventory["pants"].map(e => e);
        // low-level state variable declaration for managing animation.
        const [maxCount,setMaxCount] = useState(5);
        const [mainWindow,setMainWindow] = useState(renderShowcase(inventory));
        const [remain,setRemain] = useState({});

        // Render variety of shirt displays at a interval of 3-5 seconds
        useEffect(()=> {

            const shirtsInterval = setInterval(()=> {
            // If mainWindow contains the object that is initalized during hover.

            if (mainWindow.includes(remain)) {
                // Generate new array keeping the shirt the mouse is over.
                setMainWindow((prev)=> {
                    return prev.map((obj,index)=> {
                        if (obj === remain && index ===  mainWindow.indexOf(remain)) {
                            return remain;
                        } 
                        return [...inventory["t-shirts"], ...inventory["shirts"]].filter(item => item.name !== remain.name)[Math.floor(Math.random()*19)];
                    })
                })

            } else {
            
                // Nothing is hovered over, generate random shirt showcase
                setMainWindow(renderShowcase(inventory));
            }
        },1000);

        return () => {
            // Clear interval on render
            clearInterval(shirtsInterval); 
        }
    });

    function handleHover(event) {
        // Find the shirt object the user is hovering over, set Remain to shirt object.
        const hoverItem = [...tShirtSales, ...shirtSales].find((item) => {
            return item.name.split(' ').join('') === event.target.id;
        });
        setRemain(hoverItem);
    }


    function handleLeave(event) {
    // Set to empty object if the user is hovering over nothing.
    setRemain({});
       
    }


    return (<><motion.div
        className='background'
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1 }}
      >
        <h1><i>Our Lastest Deals... </i></h1>
        <h2>Check Out Our T-shirts! </h2>
        <div className="mainwindow">

        {/*Animate Presence of newly generated shirt displays */}
        <AnimatePresence>
            {mainWindow.map((shirt) => {
                // Wrap each shirt showcased in a Link to the shirt's product description. Covert name of product to tolerable code format.
        return (<Link  className="a" to={"/product/" + shirt.name.split(' ').join('+')}>
            {/*Animation settings */}
            <motion.span
        key={shirt.name}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1.7 }}
      >
        {/* Listener Events added to tags. Name of shirt coverted to tolerable format and used as ID for comparison in handleHover.*/}
        <div id={shirt.name.split(' ').join('')} onMouseLeave={handleLeave}  onMouseEnter={handleHover}  >
        <img alt={shirt.name} id={shirt.name.split(' ').join('')} onMouseEnter={handleHover} src={shirt.image} />


      <AnimatePresence>
        {/*Renders price on hover*/}
      <RenderPrice hover={remain} id={shirt} price={shirt.price} name={shirt.name} /> 
      </AnimatePresence>

      </div>
      
      </motion.span> 
      </Link>)
    })
    }


        </AnimatePresence>
        </div>
        <h2>Best Deals!</h2>

        <div class="categoryContainer" >
            <h2 style={{textAlign:"left", marginLeft:'100px'}}>Shirts on sale 20% off!</h2>
            <div className="flexContainer">
                {/* Formatted for future use implementation of a click to next page of clothing feature.  */}
                {Array(1).fill(0).map((pages) => {
                    {/*Filter for low priced items only */}
                    const page = [shirtSales.filter(div => {
                        return div.price < 30.99;
                    }).map((div,index) => {
                        {/* Do not display more than maxCount */}
                        if (index <= maxCount) {
                            shirtSales.splice(shirtSales.indexOf(div),1);
                            return  <Row div={div} />
                        }
                        return '';
                        }
                        ).filter(element => element !== '')];
                        
                    return <div className="row">{page}</div>;
                })}
            </div>

        </div>
        <div class="categoryContainer"  >
            <h2 style={{textAlign:"left", marginLeft:'100px'}}>Pants on sale 20% off!</h2>
            <div  className="flexContainer">
                {/* Formatted for future use implementation of a click to next page of clothing feature.  */}
                {Array(1).fill(0).map((pages) => {
                     {/*Filter for low priced items only */}
                   const page = [pantsSales.filter(div => {
                    return div.price < 40;
                }).map((div,index) => {
                     {/* Do not display more than maxCount */}
                    if (index <= maxCount) {
                        pantsSales.splice(pantsSales.indexOf(div),1);
                        return <Row  div={div} />
                    }
                    return <></>;
                    }
                    )]
                    return <div className="row">{page}</div>;
                })}
            </div>
            
        </div>
        </motion.div></>)

}

export default Home;
