import './Cart.css';
import React from 'react';
import { useSelector } from 'react-redux';
import CartItem from './CartItem';
import { cartSelector,removeCartItem } from '../../Features/Cart/cartSlice';
import { motion } from 'framer-motion';
import { useDispatch } from 'react-redux';
import { useEffect } from 'react';
import { useNavigate } from 'react-router';
function Cart ({view}) {
    const cartobj = useSelector(cartSelector);
    const navigate = useNavigate();
    const dispatch = useDispatch();
    function clearCart() {
        cartobj.forEach(obj => {
            dispatch(removeCartItem(obj));
        });
    }
    function checkout(e) {
        navigate('checkout')
    }
    if (view) {
    return (
        <motion.div
        className="cartCon"
        initial={{ opacity: 0 }}
        animate={{ transform: `scaleY(1)`,opacity: 1}}
        exit={{ opacity: 0 }}
        transition={{ duration: 0.5 }}
      ><div style={ cartobj.length > 3 ? {overflowY:'scroll'} : {}} className="cart">

        <div className="cartContainer">
            {cartobj.map(obj => {
                return  <CartItem  cartObj={obj} view={view} />;
            })}
        </div>
        <div id="container2">
            <button onClick={checkout}>Checkout</button>
            <button onClick={clearCart}>Clear Cart</button>
        </div>
    </div></motion.div>)
    }
    return '';
   
}

export default Cart;
