const initialState = '';

export const searchFilterSelector = (state) => {
    state.searchFilter;
}

const options = {
    name:'searchFilter',
    initialState: initialState,
    reducers: {
        searchTerm(state, action) {
            state = action.payload;
        }
    }
}
export const searchFilterSlice = createSlice(options)
