import React from 'react';
import './Cart.css';
import { useDispatch } from 'react-redux';
import { addQuantity, removeCartItem } from '../../Features/Cart/cartSlice';
import { useState } from 'react';
import { useEffect } from 'react';
function CartItem({cartObj, view}) {
    const dispatch = useDispatch();
    const [currentobj,setCurrentObj] = useState(cartObj);
    const [quantity,setQuantity]= useState(1);
    function handleChange(e) {
        const {value} = e.target;
        setQuantity(value)
        dispatch(addQuantity({...currentobj, quantity: quantity}));
    }
    function handleDelete() {
        dispatch(removeCartItem(cartObj));
    }
    return (<div >
        <div style={{backgroundColor:`white`}} class="cartItemContainer">
        <div style={{textAlign:`end`}}>
           <span id="textalign" onClick={handleDelete}>X</span> 
        </div>
         <img src={currentobj.image} alt={currentobj.name} />
         <div>
         <h4>{currentobj.name}</h4>
        <p>{currentobj.price}</p> 
        <label for="quantity">Quantity:</label>
        <input id="quantity" type="number" min="1" max="20" value={quantity} name="quantity" style={{width:`32px`}} onChange={handleChange} />
         </div>
       
       
    </div>
    </div>)

}
export default CartItem;
