import React from 'react';
import { motion } from "framer-motion";


function RenderPrice({price, id, hover, name}) {
    //  check for undefined
    if (hover) {
        if (hover.hasOwnProperty('name')){
            if (id.name.split(' ').join('') === hover.name.split(' ').join('')) {
                return( <motion.span
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    transition={{ duration: 1.7 }}
                  ><span><p id="price"><span style={{textDecoration:'underline',color:'red'}}><b>${price}</b></span></p>
                <p className="productname animate black small">{name}</p>
                </span>
                <p  className="click">Click to view full product</p></motion.span>)
            }
        }
    
    }

}
export default RenderPrice;
