import React from 'react';
import './Home.css';
import { Outlet, NavLink, Link } from "react-router-dom";
import { motion } from "framer-motion";
import {useEffect, useState } from 'react';
import {useSelector} from 'react-redux';
import {initialState} from '../../Features/Inventory/inventoryAPI';
import { AnimatePresence } from "framer-motion";
import {RenderPrice} from './RenderPrice';
// setShirtRender.map((item,index)=> {
//     if (setShirtRender.splice(index,1).includes(item)) {
//         const removeIndex = initialState["t-shirts"].indexOf(item);
//         return initialState["t-shirts"].splice(removeIndex,1)[Math.floor(Math.random()*removeIndex.length)];
//     }
//     return initialState["t-shirts"][Math.floor(Math.random()*10)];
// })


function Home () {
        const [mainWindow,setMainWindow] = useState([initialState["t-shirts"][Math.floor(Math.random()*10)], initialState["t-shirts"][Math.floor(Math.random()*10)],initialState["t-shirts"][Math.floor(Math.random()*10)]]);
        const [remain,setRemain] = useState();
        const summer = initialState["shirts"];
        const pantsOnSale = initialState["pants"];
        function checkForRepeatItem (array) {
            return array.map((item,index)=> {
                const removedIndex = initialState["t-shirts"].splice(index,1);
                return removedIndex[Math.floor(Math.random()*removedIndex.length)];
            })
        }
        useEffect(()=> {
            const shirt = setInterval(()=> {
            if (mainWindow.includes(remain)) {
                setMainWindow((prev)=> {
                    return prev.map((obj,index)=> {
                        if (obj === remain && index ===  mainWindow.indexOf(remain)) {
                            return remain;
                        }
                        return initialState["t-shirts"][Math.floor(Math.random()*10)];
                    })
                })
            } else {
                // const setShirtRender = [initialState["t-shirts"][Math.floor(Math.random()*10)], initialState["t-shirts"][Math.floor(Math.random()*10)],initialState["t-shirts"][Math.floor(Math.random()*10)]];
                // const renderShirts = checkForRepeatItem(setShirtRender);
                setMainWindow( [initialState["t-shirts"][Math.floor(Math.random()*10)], initialState["t-shirts"][Math.floor(Math.random()*10)],initialState["t-shirts"][Math.floor(Math.random()*10)]])
            }
        },5000);
        return () => {
            clearInterval(shirt);
        }
    });
    function handleHover(event) {
        const hoverItem = initialState["t-shirts"].find((item) => {
            return item.name.split(' ').join('') === event.target.id;
        });
        setRemain(hoverItem);
        // event.target.style.transition = '1s';
        // event.target.style.backgroundColor = 'rgba(200,0,0,0.6)';
        // event.target.style.padding = '15px';
    }
    function handleLeave(event) {
        event.target.style.backgroundColor = 'white';
        event.target.style.padding = '5px';
        setRemain('');
    }
    return (<> 
        <h2>Check out our T-shirts! </h2>
        <div className="mainwindow">
        <AnimatePresence>
            {mainWindow.map((div) => {
        return <Link className="a" to={"/products/" + div.name.split(' ').join('+')}><motion.span
        key={div.name}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1.7 }}
      ><div onMouseLeave={handleLeave}  onMouseEnter={handleHover} id={div.name.split(' ').join('')}><img  src={div.image} /><RenderPrice /></div></motion.span> </Link>
    })
    }
        </AnimatePresence>
        </div>
        <div  >
            <h2 style={{textAlign:"left", marginLeft:'100px'}}>Shirts on sale!</h2>
            <div className="row">
                {summer.map((div) => {
                    if (div.price < 30.99) {
                        return <div className="row-containers"><div><img  src={div.image} /></div><p><Link className="a2" to={"/products/" + div.name.split(' ').join('+')}>{div.name}</Link> <span style={{textDecoration:'underline',color:'red'}}>{div.price}</span></p></div>
                    }
                    }
                    )}
            </div>

        </div>
        <div  >
            <h2 style={{textAlign:"left", marginLeft:'100px'}}>Pants on sale!</h2>
            <div className="row">
                {pantsOnSale.map((div) => {
                    if (div.price < 40) {
                        return <div className="row-containers"><div><img  src={div.image} /></div><p><Link className="a2" to={"/products/" + div.name.split(' ').join('+')}>{div.name}</Link> <span style={{textDecoration:'underline',color:'red'}}>{div.price}</span></p></div>
                    }
                    }
                    )}
            </div>
            
        </div>
   </>)

}
