// })

// document.querySelectorAll('.a > div').forEach((element) => {
//     element.animate({opacity:'1'},{duration:4000,fill:'forwards'});
// })
// document.querySelectorAll('.a > div').forEach((element) => {
//                     if (element.id !== remain.name.split(' ').join('')) {
//                         element.animate({opacity:'0'},{duration:1000,fill:'forwards'});
//                     }
//                 })
            
export default Home;
