import React from 'react';
import './Root.css';
import { Outlet, NavLink, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import {useEffect, useState} from 'react';
import {initialState} from '../../Features/Inventory/inventoryAPI';
import { useDispatch } from 'react-redux';
import { searchTerm } from '../../Features/ProductFilter/searchFilterSlice';
import Cart from '../Cart/Cart';
function grabPhotos(name) {
    fetch(`https://www.dogpile.com/serp?qc=images&q=${name}`).then((response)=> response.text()).then(data => {
    const string = "https://'tse4.mm.bing.net";
    const regex = /(["'].*bing(.*)Api["'])/g
    data.match(regex);
    console.log(data.match(regex))
})
}
function Root ({setTerm, term}) {
    const firstRender = useNavigate();
    const dispatch = useDispatch();
    const [viewCart,setViewCart] = useState(false);
    function handleText (event) {
        const {name,value} = event.target;
        dispatch(searchTerm(value));
        setTerm((prev) => {
            return { ...prev, [name]: value}
        })
    }

    useEffect(()=> {
        firstRender('/home');
    },[])
    return (<>
     <motion.div
            className="container text-center  bg-black"
            initial={{ opacity: 0, transform: `translate(100px)` }}
            animate={{ opacity: 1 , transform: `translate(0px)`}}
            exit={{ opacity: 0 }}
            transition={{ duration: 1 }}
          >
    <nav className="navigation">
        <span className="logoContainer"><b id="logo">Bargain Boutique</b>
        <ul>
            <li><NavLink className="navlinks" to="/home">Home</NavLink></li>
            <li><NavLink className="navlinks" to="/products">Products</NavLink></li>
            <li><NavLink className="navlinks" to="/about#contacts">Contact</NavLink></li>
            <li><NavLink className="navlinks" to="/about">About</NavLink></li>
        </ul>
        </span>
        <div id="searchContainer">
                <input type="text" name="search" value={term['search']} onChange={handleText} />
                <button onClick={() => setViewCart(!viewCart)}>Cart</button>
                
        </div>

            
    </nav>
    <Outlet />
    </motion.div> </>)

}

export default Root;
