import React from 'react';
import { useSelector,useDispatch  } from 'react-redux';
import { allFilteredClothing } from '../../Features/Inventory/inventorySlice';
import { loadData } from '../../Features/Inventory/inventorySlice';
import { useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import './Productpage.css';
import ProductItems from './ProductItems';
import { searchFilterSelector } from '../../Features/ProductFilter/searchFilterSlice';
function ProductPage ({term}) {
    const AllProducts = useSelector(allFilteredClothing);
    const printTerm = useSelector(searchFilterSelector);
    
    return (<><motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1.7 }}
      >
         <h2>{!printTerm ? "All Catagories" : `"Results for ${printTerm}"`} </h2>
        <div className="productContainer">
    <div className="productdisplay">
        {Object.values(AllProducts).map((category,index) => {
            const categoryArray = category.map((product) => {
                if (product.name.toLowerCase().includes(printTerm.toLowerCase())) {
                    return <motion.div
                    initial={{ opacity: 0.4, transform:`scale(1.2)` }}
                    animate={{ opacity: 1, transform:`scale(1)` }}
                    exit={{ opacity: 0 }}
                    transition={{ duration: 1.7 }}
                  ><ProductItems name={product.name} price={product.price} color={product.color} image={product.image}/></motion.div>
                }
                return null;
            });
            const arrayLength = categoryArray.filter(item => item !== null);
            const each = <div className="flexrow">{categoryArray}</div>
            return [<h2>{arrayLength.length ? Object.keys(AllProducts)[index].toUpperCase() : ''}</h2>,<motion.div
            initial={{  transform:`scale(1.2)` }}
            animate={{transform:`scale(1)` }}
            exit={{ opacity: 0 }}
            transition={{ duration: 1.7 }}
          ><div className="flexrow">{each}</div></motion.div>]
        })}
       
    </div>
    </div></motion.div></>)
}

export default ProductPage;
