import './Cart.css';
import React from 'react';
import { useSelector } from 'react-redux';
import CartItem from './CartItem';
import { cartSelector } from '../../Features/Cart/cartSlice';
import { motion } from 'framer-motion';
function Cart ({view}) {
    const cartobj = useSelector(cartSelector);
    if (view) {
    return (
        <motion.div
        className="cart"
        initial={{ opacity: 0 }}
        animate={{ transform: `scaleY(1)`,opacity: 1}}
        exit={{ opacity: 0 }}
        transition={{ duration: 0.5 }}
      ><div style={{position:'sticky',zIndex:'20',top:'20px'}} className="cart">

        <div className="cartContainer">
            {cartobj.map(obj => {
                return  <CartItem cartObj={obj} view={view} />;
            })}
        </div>
        <div id="container2">
            <button>Checkout</button>
        </div>
    </div></motion.div>)
    }
   
}

export default Cart;
