import React from 'react';
import './ProductsItems.css';
import { Link } from 'react-router-dom';
function ProductItems ({name, price, image, color}) {
    return (<div className="productgrid">
        <div>
            <img alt={name} src={image} />
        </div>
        <p><Link to={"/products/" + name.split(' ').join('+')}>{name}</Link>
        <p>{price}</p>
        <p>Color:{color}</p></p>
       
    </div>)
}

export default ProductItems;
