import React from 'react';
import { motion } from 'framer-motion';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import { addQuantity, cartSelector } from '../../Features/Cart/cartSlice';
import  { addCart} from '../../Features/Cart/cartSlice';
import { useState,useEffect } from 'react';
function ProductDescription({producttitle,productobj}) {
    const cart = useSelector(cartSelector);
    const dispatch = useDispatch();
    useEffect(()=> {

    },[dispatch])
    function handleAdd(event) {
        const cartHasItem = cart.find(item => item.name.includes(productobj.name))
        if (!cartHasItem) {
             dispatch(addCart({...productobj, quantity:1, setQ:null}));
        }  else {
            if (cartHasItem.setQ) {
                cartHasItem.setQ(q => +q + 1)
            }
        }
            
       
    }
    return (<motion.div
        className='background'
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1 }}
      ><div className="flexbox"> 

    <div>

<h1>{producttitle}</h1>
<img src={productobj.image} />
    </div>
    <div className="userInput">
        <span onClick={handleAdd} className="animate black"><b>Add to cart</b></span>
        <h3><s>price:  2,674,431 dollars</s></h3>
        <h2><span style={{color:'red'}}>Price: ${productobj.price}</span></h2>
        <h2>Color:{productobj.color}</h2>
        <h3>Description: {productobj.description }</h3>
    </div>


</div> </motion.div>)
}

export default ProductDescription;
