import React from 'react';
import { motion } from 'framer-motion';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import { cartSelector } from '../../Features/Cart/cartSlice';
import  { addCart,addQuantity } from '../../Features/Cart/cartSlice';
import { useState } from 'react';
function ProductDescription({producttitle,productobj}) {
    const [quantity,setQuantity]= useState(1);
    const cart = useSelector(cartSelector);
    const dispatch = useDispatch();
    function handleAdd(event) {
        const cartHasItem = cart.find(item => item.name.includes(productobj.name))
        if (!cartHasItem) {
             dispatch(addCart({...productobj, quantity:quantity, setQ: setQuantity}));
        } else {
            cartHasItem.setQ((prev) => {
                return +prev + 1;
                })
            
          
        }
       
    }
    return (<motion.div
        className='background'
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1 }}
      ><div className="flexbox"> 

    <div>

<h1>{producttitle}</h1>
<img src={productobj.image} />
    </div>
    <div className="userInput">
        <button onClick={handleAdd}>Add to cart</button>
        <h3><s>price:  2,674,431 dollars</s></h3>
        <h2><span style={{color:'red'}}>Price: ${productobj.price}</span></h2>
        <h2>Color:{productobj.color}</h2>
        <h3>Description: {productobj.description}</h3>
    </div>


</div> </motion.div>)
}

export default ProductDescription;
