import React from 'react';
import './Home.css';
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import {useEffect, useState } from 'react';
// import {useSelector} from 'react-redux';
import {initialState} from '../../Features/Inventory/inventoryAPI';
import { AnimatePresence } from "framer-motion";
import RenderPrice from './RenderPrice';
import './BargainB.png';
// setShirtRender.map((item,index)=> {
//     if (setShirtRender.splice(index,1).includes(item)) {
//         const removeIndex = initialState["t-shirts"].indexOf(item);
//         return initialState["t-shirts"].splice(removeIndex,1)[Math.floor(Math.random()*removeIndex.length)];
//     }
//     return initialState["t-shirts"][Math.floor(Math.random()*10)];
// })
function renderRandomMainShowCase(number,exclude ={name:'nothing'}) {
    return Array(number).fill(initialState["t-shirts"].filter(item => item.name !== exclude.name)[Math.floor(Math.random()*10)]);
}
//Array(number).fill(initialState["t-shirts"].filter(item => item.name !== exclude.name)[Math.floor(Math.random()*10)]);
function Home () {
        const [mainWindow,setMainWindow] = useState(renderRandomMainShowCase(2));
        const [remain,setRemain] = useState({});
        const summer = initialState["shirts"];
        const pantsOnSale = initialState["pants"];
        useEffect(()=> {
            const shirt = setInterval(()=> {
            if (mainWindow.includes(remain)) {
                setMainWindow((prev)=> {
                    return prev.map((obj,index)=> {
                        if (obj === remain && index ===  mainWindow.indexOf(remain)) {
                            return remain;
                        } 
                        return renderRandomMainShowCase(1)[0];
                    })
                })
            } else {
                // setShirtRender.push(initialState["t-shirts"].filter(item => item !== setShirtRender[0])[Math.floor(Math.random()*9)]);
                // setShirtRender.push(initialState["t-shirts"].filter(item => item !== setShirtRender[0] && item !== setShirtRender[1] )[Math.floor(Math.random()*8)]);
                const standardShowcase = [renderRandomMainShowCase(1)[0],renderRandomMainShowCase(1)[0],renderRandomMainShowCase(1)[0]]
                setMainWindow(standardShowcase);
            }
        },2000);
        return () => {
            clearInterval(shirt); 
        }
    });
    function handleHover(event) {
        const hoverItem = initialState["t-shirts"].find((item) => {
            return item.name.split(' ').join('') === event.target.id;
        });
        setRemain(hoverItem);
        // event.target.style.transition = '1s';
        // event.target.style.backgroundColor = 'rgba(200,0,0,0.6)';
        // event.target.style.padding = '15px';
    }
    function handleLeave(event) {
    setRemain({});
       
    }
    return (<><motion.div
        className='background'
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1 }}
      >
        <h1><i>Our Lastest Deals...</i></h1>
        <h2>Check Out Our T-shirts! </h2>
        <div className="mainwindow">
        <AnimatePresence>
            {mainWindow.map((div) => {
        return (<Link  className="a" to={"/product/" + div.name.split(' ').join('+')}><motion.span
        key={div.name}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1.7 }}
      ><div id={div.name.split(' ').join('')} onMouseLeave={handleLeave}  onMouseEnter={handleHover}  ><img alt={div.name} id={div.name.split(' ').join('')} onMouseLeave={handleLeave}  onMouseEnter={handleHover} src={div.image} />
      <AnimatePresence>
      <RenderPrice hover={remain} id={div} price={div.price} name={div.name} /> 
      </AnimatePresence>
      </div>
      
      </motion.span> 
      </Link>)
    })
    }
        </AnimatePresence>
        </div>
        <h2>Best Deals!</h2>
        <div  >
            <h2 style={{textAlign:"left", marginLeft:'100px'}}>Shirts on sale!</h2>
            <div className="row">
                {summer.map((div) => {
                    if (div.price < 30.99) {
                        return <div className="row-containers"><div><img  alt={div.name} src={div.image} /></div><p><Link className="a2" to={"/product/" + div.name.split(' ').join('+')}>{div.name}</Link> <span style={{textDecoration:'underline',color:'red'}}>${div.price}</span></p></div>
                    }
                    return '';
                    }
                    )}
            </div>

        </div>
        <div  >
            <h2 style={{textAlign:"left", marginLeft:'100px'}}>Pants on sale!</h2>
            <div className="row">
                {pantsOnSale.map((div) => {
                    if (div.price < 40) {
                        return <div className="row-containers"><div><img  alt={div.name} src={div.image} /></div><p><Link className="a2" to={"/product/" + div.name.split(' ').join('+')}>{div.name}</Link> <span style={{textDecoration:'underline',color:'red'}}>${div.price}</span></p></div>
                    }
                    return <></>;
                    }
                    )}
            </div>
            
        </div>
        </motion.div></>)

}
// document.querySelectorAll('.a > div').forEach((element) => {
//     element.animate({opacity:'1'},{duration:4000,fill:'forwards'});
// })

// document.querySelectorAll('.a > div').forEach((element) => {
//     element.animate({opacity:'1'},{duration:4000,fill:'forwards'});
// })
// document.querySelectorAll('.a > div').forEach((element) => {
//                     if (element.id !== remain.name.split(' ').join('')) {
//                         element.animate({opacity:'0'},{duration:1000,fill:'forwards'});
//                     }
//                 })
            
export default Home;
