import React from 'react';
import { useSelector,useDispatch  } from 'react-redux';
import { allFilteredClothing } from '../../Features/Inventory/inventorySlice';
import { loadData } from '../../Features/Inventory/inventorySlice';
import { useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import './Productpage.css';
import ProductItems from './ProductItems';
import { searchFilterSelector } from '../../Features/ProductFilter/searchFilterSlice';
function ProductPage ({term}) {
    const AllProducts = useSelector(allFilteredClothing);
    const printTerm = useSelector(searchFilterSelector);
    
    return (<><motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1.7 }}
      >
         <h2>{!printTerm ? "All Catagories" : `"Results for ${printTerm}"`} </h2>
        <div className="productContainer">
    <div className="productdisplay">
        {Object.values(AllProducts).map((category,index) => {
            let count = 0
            const row = [];
            const each = <div className="flexrow">{category.map((product) => {
                if (product.name.toLowerCase().includes(printTerm.toLowerCase())) {
                    if (count < 5) {
                        row.push(<ProductItems name={product.name} price={product.price} color={product.color} image={product.image}/>)
                    } else {
                        return <ProductItems name={product.name} price={product.price} color={product.color} image={product.image}/>
                    }
                    count += 1;
                }
            })}</div>
            return [<h2>{}</h2>,<div className="flexrow">{row}</div>,each]
        })}
       
    </div>
    </div></motion.div></>)
}

export default ProductPage;
