import React from 'react';
import './Root.css';
import { Outlet, NavLink, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import {useEffect, useState} from 'react';
import { useDispatch } from 'react-redux';
import { searchTerm } from '../../Features/ProductFilter/searchFilterSlice';
import { cartSelector} from '../../Features/Cart/cartSlice';
import { useSelector } from 'react-redux';
import Cart from '../Cart/Cart';
import {AnimatePresence} from 'framer-motion';
function Root ({setTerm, term}) {
    const firstRender = useNavigate();
    const dispatch = useDispatch();
    const [viewCart,setViewCart] = useState(false);
    const [notif,setNotif] = useState(0);
    const [mobileView,setMobileView] = useState('inherit');
    const cart = useSelector(cartSelector);
    
    function handleText (event) {
        const {name,value} = event.target;
        dispatch(searchTerm(value));
        setTerm((prev) => {
            return { ...prev, [name]: value}
        })
    }

    useEffect(()=> {

        firstRender('/home');
    },[])
    useEffect(()=> {
        if (cart.length) {
            setNotif(cart.length);
        } else {
            setNotif(cart.length);
        }
    },[cart.length])
    return (<>
     <motion.div
            className="container text-center  bg-black"
            initial={{ opacity: 0, transform: `translate(100px)` }}
            animate={{ opacity: 1 , transform: `translate(0px)`}}
            exit={{ opacity: 0 }}
            transition={{ duration: 1 }}
          >
    <nav className="navigation" >
    <button onClick={(e) => {
        if (mobileView === 'inherit') {
            setMobileView('none')
        } else {
            
        }
    }}><i class="fa fa-bars" aria-hidden="true"></i></button>
        <div style={{display:mobileView}} className="navigation"> 
            <span className="logoContainer"><b id="logo"><NavLink className="navlinks" to="/home">Bargain Boutique</NavLink></b>
            <ul>
                <li className=""><NavLink className="navlinks" to="/home"><span className="animate white">Home</span></NavLink></li>
                <li ><NavLink className="navlinks" to="/products"><span className="animate white">Products</span></NavLink></li>
                <li ><NavLink className="navlinks" to="/about#contacts"><span className="animate white">Contact</span></NavLink></li>
                <li ><NavLink className="navlinks" to="/about"><span className="animate white">About</span></NavLink></li>
            </ul>
            </span>
            <div id="searchContainer">
                    <input type="text" name="search" value={term['search']} onChange={handleText} />
                    <button id="cartButton" onClick={() => { 
                        
                        setViewCart(!viewCart);
                        
                    }} ><b><i style={{fontSize:'23px'}} class="fa fa-shopping-cart"></i></b>{ notif ? <i className="notification">{notif}</i> : ''}</button>
                    
            </div>
        </div>
       

            
    </nav>
    <AnimatePresence >
    <Cart view={viewCart}  />
    </AnimatePresence >
    <Outlet />
    </motion.div> </>)

}

export default Root;
