import React from 'react';
import './Cart.css';
import { useDispatch } from 'react-redux';
import { addQuantity, removeCartItem } from '../../Features/Cart/cartSlice';
import { useState } from 'react';
import { useEffect } from 'react';
function CartItem({cartbj, view,cart, quantity, setQuantity}) {
    const cartObj = cart.find(item => item.name === cartbj.name);
    const dispatch = useDispatch();
    function handleChange(e) {
        const {value} = e.target;
        setQuantity(value)
        dispatch(addQuantity({...cartObj, quantity: value}));
    }
    function handleDelete() {
        dispatch(removeCartItem(cartObj));
    }

    if (view) {
    
    return (<div >
        <div style={{backgroundColor:`white`}} class="cartItemContainer">
        <div style={{textAlign:`end`}}>
           <span id="textalign" onClick={handleDelete}>X</span> 
        </div>
         <img src={cartObj.image} alt={cartObj.name} />
         <div>
         <h4>{cartObj.name}</h4>
        <p>{cartObj.price}</p> 
        <label for="quantity">Quantity:</label>
        <input id="quantity" type="number" min="1" value={quantity} name="quantity" style={{width:`32px`}} onChange={handleChange} />
         </div>
       
       
    </div>
    </div>)

    }
}
export default CartItem;
