import {initialState} from './inventoryAPI';
import {createSlice } from "@reduxjs/toolkit";
import { searchFilterSelector } from '../ProductFilter/searchFilterSlice';
export const loadData = () => {
    return {type:'inventory/loadState', payload: initialState};
}

export const inventorySelector = (state) => {
    return state.inventory;
}

const options = {
    name:'inventory',
    initialState:initialState,
    reducer: {
        loadState (state,action) {
            state.initialState = action.payload;
        }
    }
}

export const allFilteredClothing = (state) => {
    const clothing = inventorySelector(state);
    const searchTerm = searchFilterSelector(state);
    return clothing;
}

export const inventorySlice = createSlice(options);
