import React from 'react';
import { useSelector,useDispatch  } from 'react-redux';
import {inventorySelector} from '../../Features/Inventory/inventorySlice';
import { loadData } from '../../Features/Inventory/inventorySlice';
import { useEffect } from 'react';
import './Productpage.css';
import ProductItems from './ProductItems';
function ProductPage () {
    const AllProducts = useSelector(inventorySelector);

    return (<div className="Productdisplay">
        {Object.values(AllProducts).map(category => {
            let count = 0
            const row = <div className="flexrow">{category.map((product, index) => {
                if (count < 5) {
                    row.push(<ProductItems name={product.name} price={product.price} color={product.color} image={product.image}/>)
                }
                count += 1;
                return <ProductItems name={product.name} price={product.price} color={product.color} image={product.image}/>
            })}</div>
            return 
        })}
       
    </div>)
}

export default ProductPage;
