import React from 'react';
import './ProductsItems.css';
import { Link } from 'react-router-dom';
function ProductItems ({name, price, image, color}) {
    return (<Link className="a2" to={"/products/" + name.split(' ').join('+')}><div className="productgrid">
         
        <div >
            <img alt={name} src={image} />
        </div>

        <h2 className="productDisplays">
           {name}
        <h2>${price}</h2>
        <p>Color:{color}</p>
        <button>Add to Cart</button>

        </h2>
       
    </div></Link>)
}

export default ProductItems;
