import React from 'react';
import './Cart.css';
import { useDispatch } from 'react-redux';
import { addQuantity, removeCartItem } from '../../Features/Cart/cartSlice';
import { useState } from 'react';
function CartItem({cartObj, view}) {
    const dispatch = useDispatch();
    const [quantity,setQuantity]= useState(1);
    function handleChange(e) {
        const {name,value} = e.target;
        setQuantity(value)
        dispatch(addQuantity({...cartObj, quantity: value}))
    }
    function handleDelete() {
        dispatch(removeCartItem(cartObj));
    }

    if (view) {
    
    return (<div >
        <div style={{backgroundColor:`white`}} class="cartItemContainer">
        <div id="textalign">
           <span id="textalign" onClick={handleDelete}>X</span> 
        </div>
         <img src={cartObj.image} />
        <h4>{cartObj.name}</h4>
        <p>{cartObj.price}</p> 
        <input type="number" min="1" value={quantity} name="quantity" style={{width:`32px`}} onChange={handleChange} />
    </div>
    </div>)

    }
}
export default CartItem;
