import React from 'react';
import { useSelector,useDispatch  } from 'react-redux';
import {inventorySelector} from '../../Features/Inventory/inventorySlice';
import { loadData } from '../../Features/Inventory/inventorySlice';
import { useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import './Productpage.css';
import ProductItems from './ProductItems';
function ProductPage () {
    const AllProducts = useSelector(inventorySelector);

    return (<><motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        transition={{ duration: 1.7 }}
      >
         <h2>All Categories</h2>
        <div className="productContainer">
    <div className="productdisplay">
        {Object.values(AllProducts).map(category => {
            let count = 0
            const row = [];
            const each = <div className="flexrow">{category.map((product, index) => {
                if (count < 5) {
                    row.push(<ProductItems name={product.name} price={product.price} color={product.color} image={product.image}/>)
                } else {
                    return <Link className="a2" to={"/products/" + product.name.split(' ').join('+')}>{product.name}<ProductItems name={product.name} price={product.price} color={product.color} image={product.image}/></Link>
                }
                count += 1;
            })}</div>
            return [<div className="flexrow">{row}</div>,each]
        })}
       
    </div>
    </div></motion.div></>)
}

export default ProductPage;
