import React from 'react';
import './ProductsItems.css';
import { Link } from 'react-router-dom';
function ProductItems ({name, price, image, color}) {
    
    return (<div className="productgrid">
         
        <div >
        <h2>{name}</h2>
            <img alt={name} src={image} />
        </div>

        <p className="productDisplays">
        <h2>${price}</h2>
        <p>Color:{color}</p>
        <Link className="a2" to={"/product/" + name.split(' ').join('+')}>Click to View</Link>

        </p>
       
    </div>)
}

export default ProductItems;
