import React from 'react';
import './Home.css';
import { Outlet, NavLink } from "react-router-dom";
import { motion } from "framer-motion";
import {useEffect, useState} from 'react';
import {useSelector} from 'react-redux';
import {initialState} from '../../Features/Inventory/inventoryAPI';
function grabPhotos(obj,setFunc) {
    setTimeout(()=>{
        fetch(`https://www.dogpile.com/serp?qc=images&q=${obj.name.replace(' ','+')}`).then((response)=> response.text()).then(data => {
            const string = "https://'tse4.mm.bing.net";
            const regex = /(["'].*bing(.*)Api["'])/g
            data.match(regex);
            let photo = data.match(regex)[Math.floor(Math.random()*10)];
            setFunc((prev) => {
                return [...prev, {...obj, photourl:photo}];
            });
        }) 
    },2000)
    
}
function Home () {
    const mainWindow = [initialState["t-shirts"][Math.floor(Math.random()*10)], initialState["t-shirts"][Math.floor(Math.random()*10)],initialState["t-shirts"][Math.floor(Math.random()*10)]];
    const [main,setMain] = useState([]);
    useEffect(()=> {
    grabPhotos(mainWindow[0],setMain);
    },[]);
    return (<> <motion.div
        className="container text-center  bg-black"
        initial={{ opacity: 0, transform: `translate(100px)` }}
        animate={{ opacity: 1 , transform: `translate(0px)`}}
        exit={{ opacity: 0 }}
        transition={{ duration: 1 }}
      >
        <h2>On sale!</h2>
        <div className="main-window">
            {}
        </div>
    </motion.div> </>)

}

export default Home;
