import React from 'react';
import './Home.css';
import { Outlet, NavLink, Link } from "react-router-dom";
import { motion } from "framer-motion";
import {useEffect, useState } from 'react';
import {useSelector} from 'react-redux';
import {initialState} from '../../Features/Inventory/inventoryAPI';
import { AnimatePresence } from "framer-motion";
function Home () {
        const [mainWindow,setMainWindow] = useState([initialState["t-shirts"][Math.floor(Math.random()*10)], initialState["t-shirts"][Math.floor(Math.random()*10)],initialState["t-shirts"][Math.floor(Math.random()*10)]]);
        const [remain,setRemain] = useState();
        const summer = initialState["shirts"];
        useEffect(()=> {
            const shirt = setInterval(()=> {
            if (mainWindow.includes(remain)) {
                setMainWindow((prev)=> {
                    return prev.map((obj,index)=> {
                        if (obj === remain && index === prev.indexOf(remain)) {
                            return remain;
                        }
                        return initialState["t-shirts"][Math.floor(Math.random()*10)];
                    })
                })
            } else {
                setMainWindow([initialState["t-shirts"][Math.floor(Math.random()*10)], initialState["t-shirts"][Math.floor(Math.random()*10)],initialState["t-shirts"][Math.floor(Math.random()*10)]])
            }
        },5000);
        return () => {
            clearInterval(shirt);
        }
    });
    function handleHover(event) {
        const hoverItem = initialState["t-shirts"].find((item) => {
            return item.name.split(' ').join('') === event.target.id;
        });
        setRemain(hoverItem);
        event.target.style.transition = '1s';
        event.target.style.backgroundColor = 'rgba(255,255,0,0.6)';
    }
    function handleLeave(event) {
        event.target.style.backgroundColor = 'white';
        setRemain('');
    }
    return (<> 
        <h2>Check out our T-shirts! </h2>
        <div className="mainwindow">
        <AnimatePresence>
            {mainWindow.map((div) => {
        return <Link className="a" to={"/products/" + div.name.split(' ').join('+')}><div onMouseLeave={handleLeave}  onMouseEnter={handleHover} id={div.name.split(' ').join('')}><img  src={div.image} /></div> </Link>
    })
    }
        </AnimatePresence>
        </div>
        <div  >
            <h2 style={{textAlign:"left", marginLeft:'100px'}}>Looking for style? Look no further...</h2>
            <div className="row">
                {summer.map((div) => {
            return <div className="row-containers"><div><img  src={div.image} /></div><p><Link className="a2" to={"/products/" + div.name.split(' ').join('+')}>{div.name}</Link> <span style={{textDecoration:'underline'}}>{div.price}</span></p></div>
    })}
            </div>
        </div>
   </>)

}
