import React from 'react';
import './Cart.css';
import { useDispatch } from 'react-redux';
import { addQuantity, removeCartItem } from '../../Features/Cart/cartSlice';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { useEffect } from 'react';
function CartItem({cartObj, view,cart}) {
    const dispatch = useDispatch();
    const [quantity,setQuantity]= useState(1);
    useEffect(()=> {
        dispatch(addQuantity({...cartObj, setQ: setQuantity}))
    },[cartObj.setQ === null])
    function handleChange(e) {
        const {value} = e.target;
        setQuantity(value);
        dispatch(addQuantity({...cartObj, quantity:value, setQ: setQuantity}))
    }
    function handleDelete() {
        dispatch(removeCartItem(cartObj));
    }

    
    return (<div >
        <div style={{backgroundColor:`white`}} class="cartItemContainer">
        <div style={{textAlign:`end`}}>
           <span id="textalign" onClick={handleDelete}>X</span> 
        </div>
         <img src={cartObj.image} alt={cartObj.name} />
         <div>
         <h4><Link className="a2" to={"/product/" + cartObj.name.split(' ').join('+')}>{cartObj.name}</Link></h4>
        <p>{cartObj.price}</p> 
        <label for="quantity">Quantity:</label>
        <input id="quantity" type="number" min="1" value={quantity} name="quantity" style={{width:`32px`}} onChange={handleChange} />
         </div>
       
       
    </div>
    </div>)

    
}
export default CartItem;
