import React from 'react';
import './Cart.css';
import { useDispatch } from 'react-redux';
import { addQuantity } from '../../Features/Cart/cartSlice';
import { useState } from 'react';
function CartItem({cartObj, view}) {
    const dispatch = useDispatch();
    const [quantity,setQuantity]= useState(0);
    function handleChange(e) {
        const {name,value} = e.target;
        setQuantity(value)
        dispatch(addQuantity({...cartObj, quantity: value}))
    }
    if (view) {
    
    return (<div>
        <div style={{backgroundColor:`white`}} class="cartItemContainer">
         <img src={cartObj.image} />
        <h4>{cartObj.name}</h4>
        <p>{cartObj.price}</p> 
        <input type="number" min="1" value={quantity} name="quantity" style={{width:`32px`}} onChange={handleChange} />
    </div>
    </div>)

    }
}
export default CartItem;
