import React from 'react';
import './Cart.css';
import { useDispatch } from 'react-redux';
import { addQuantity } from '../../Features/Cart/cartSlice';
function CartItem({cartObj, view}) {
    const dispatch = useDispatch();
    function handleChange(e) {
        const {name,value} = e.target;
        dispatch(addQuantity({...cartObj, [name]: value}))
    }
    if (view) {
    
    return (<div class="cartItemContainer">
        {/* <img src={cartObj.image} />
        <h4>{cartObj.name}</h4>
        <p>{cartObj.price}</p> */}
        <p>Testing</p>
        <input type="number" min="1" name="quantity" style={{width:`20px`}} onChange={handleChange} />
    </div>)
    }
}
export default CartItem;
