import React from 'react';
import { Routes, Route, useParams } from 'react-router-dom';
import './ProductView.css';
import ProductDescription from '../ProductView/ProductDescription';
import { useSelector } from 'react-redux';
import { allFilteredClothing } from '../../Features/Inventory/inventorySlice';
function ProductView() {
    
    const {product} = useParams();
    const productview = product.split('+').join(' ');
    const productObj = useSelector(allFilteredClothing);
    
    const productobj = productObj.map(item => {
        const productCategory = item.find(p => p.name === productview);
        if (productCategory) {
            return item;
        }
        return null;
    });
    productObj = productObj.filter(p => p !== null);
    
    return (<div>
        {JSON.stringify(productObj)}
       <ProductDescription producttitle={productview} />
    </div>)

}

export default ProductView;
