import React from 'react';
import './Home.css';
import { Outlet, NavLink, Link } from "react-router-dom";
import { motion } from "framer-motion";
import {useEffect, useState } from 'react';
import {useSelector} from 'react-redux';
import {initialState} from '../../Features/Inventory/inventoryAPI';

function Home () {
        const [mainWindow,setMainWindow] = useState([initialState["t-shirts"][Math.floor(Math.random()*10)], initialState["t-shirts"][Math.floor(Math.random()*10)],initialState["t-shirts"][Math.floor(Math.random()*10)]]);
        useEffect(()=> {
            const shirt = setInterval(()=> {
                document.getforEach((item) => {
                    item.animate({opacity:'0'},{duration:1000,fill:'forwards'});
                })
                
            setMainWindow([initialState["t-shirts"][Math.floor(Math.random()*10)], initialState["t-shirts"][Math.floor(Math.random()*10)],initialState["t-shirts"][Math.floor(Math.random()*10)]])
        },10000);
        return () => {
            clearInterval(shirt);
        }
    });
    const shirts = mainWindow.map((div,index) => {
        if (index === 1) {
            return <Link className="a" to={"/products/" + div.name.split(' ').join('+')}><div><img  src={div.image} /></div></Link>
        }
        return <Link className="a" to={"/products/" + div.name.split(' ').join('+')}><div><img  src={div.image} /></div></Link>
    })
    return (<> <motion.div
        className="container text-center  bg-black"
        initial={{ opacity: 0, transform: `translate(100px)` }}
        animate={{ opacity: 1 , transform: `translate(0px)`}}
        exit={{ opacity: 0 }}
        transition={{ duration: 1 }}
      >
        <h2>Check out our newest products!</h2>
        <div className="mainwindow">
            {shirts}
        </div>
    </motion.div> </>)

}

export default Home;
